import './App.css';
import React from 'react';
import Button from './Button';

class App extends React.Component {

  constructor() {
    super()

    this.state = {
      cliquesBotao1: 0,
      colorBtn1: 'green',
      cliquesBotao2: 0,
      cliquesBotao3: 0,
    }

    this.handleClick1 = this.handleClick1.bind(this);
    this.handleClick2 = this.handleClick2.bind(this);
    this.handleClick3 = this.handleClick3.bind(this);
  }

  handleClick1() {
    this.setState((state) => ({
      cliquesBotao1: state.cliquesBotao1 + 1,
    }))
    if (this.state.cliquesBotao1 % 2 === 0){
      this.setState((state) => ({
        colorBtn1: state.colorBtn1 = 'green',
      }))
    } else {
      this.setState((state) => ({
        colorBtn1: state.colorBtn1 = 'red',
      }))
    }
  }

  handleClick2() {
    this.setState((state) => ({
      cliquesBotao2: state.cliquesBotao2 + 1,
    }))
  }
  handleClick3(){
   this.setState((state) => ({
     cliquesBotao3: state.cliquesBotao3 + 1,
   }))
  }

  render() {
    return (
      <div>
          <Button onClick= {this.handleClick1}>Button 1</Button>
          <Button onClick= {this.handleClick2}>Button 2</Button>
          <Button onClick= {this.handleClick3}>Button 3</Button>
      </div>

    )
  }

}

export default App;
