import './App.css';
import React from 'react';
import PersonalInformation from './components/Personal.Fieldset';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      name: '',
      email: '',
      cpf: '',
      adress: '',
      stateName: '',
      typeAdress: '',
  }

  this.handleChange = this.handleChange.bind(this)
  }

  handleChange({target}) {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    if (name === 'name') value.toUpperCase();
    this.setState({
        [name]: value,
    });

    alert('teste');
}
  render() {
    return (
      <main>
        <header className="App-header">
          <h1>Form Curriculum</h1>
        </header>
        <PersonalInformation handleChange={this.handleChange} />
      </main>
    );
  }  
}

export default App;
