import './App.css';
import React from 'react';
import PersonalInformation from './components/Personal.Fieldset';
import states from './dataStates';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      name: '',
      email: '',
      cpf: '',
      adress: '',
      stateName: '',
      typeAdress: '',
      resume: '',
      description: '',
      office: '',
      enterMouse: 0,
  }

  this.handleChange = this.handleChange.bind(this);
  this.mouseEnter = this.mouseEnter.bind(this);
  }

  mouseEnter() {
    if (this.state.enterMouse > 0) alert('Carefully fill in this information')
    this.setState((state) => ({
      enterMouse: state.enterMouse + 1
    }))
  }

  handleChange({target}) {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
        [name]: value,
    });

}
  render() {
    return (
      <main>
        <header className="App-header">
          <h1>Form Curriculum</h1>
        </header>
        <PersonalInformation 
          handleChange={this.handleChange} 
          namePerson={this.state.name.toUpperCase()} 
          email={this.state.email} adress={this.state.adress}
          cpf={this.state.cpf} state={this.state.stateName}
          description={this.state.description} 
          resume={this.state.resume} office={this.state.office}
          mouseEnter={this.mouseEnter}/>
      </main>
    );
  }  
}

export default App;
