import React from 'react';
import './App.css';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      imageDog: '',
      loading: true,
    };

    this.fetchDog = this.fetchDog.bind(this);
    this.renderDog = this.renderDog.bind(this);
  }

  componentDidMount() {
    this.fetchDog();
  }

  async fetchDog() {
    const result = await fetch('https://dog.ceo/api/breeds/image/random');
    const resultJson = await result.json();
    const { message } = resultJson;
    this.setState({
      imageDog: message,
    });
  }

  renderDog() {
    const { imageDog } = this.state;
    return (
      <div>
        <img src={ imageDog } alt="Dog" />
        <button type="button">Add Dog</button>
      </div>
    );
  }

  render() {
    const { imageDog } = this.state;
    return (
      <div>
        <h1>Exercise Fetch Dogs</h1>
        {imageDog ? this.renderDog() : 'Loading'}
      </div>
    );
  }
}

export default App;
